INSERT INTO champions (name, role, lore, image_url) VALUES
                                                        ('Aatrox', 'Lutador', 'Uma antiga e terrível arma de guerra, Aatrox foi imbuído de vida por forças misteriosas em runeterra. Ele usa sua grande espada com habilidade e fúria, desencadeando uma dor massiva e morte a cada golpe.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Aatrox_0.jpg'),
